import React, { useState } from "react";
import { HashRouter as Routr, Route, Switch } from "react-router-dom";
import Auth from " ../routes/Auth";
import Home from " ../routes/Home";

export default () => {
  const [isLoggeIn, seIsLoggedIn] = useState(false);
  return (
    <Router exact path="/">
      <Switch>
        {isLoggeIn ? (
          <>
            {/* fragment */}
            <Router exact path="/">
              <Home />
            </Router>
          </>
        ) : (
          <Route>
            <Auth />
          </Route>
        )}
      </Switch>
    </Router>
  );
};
